/**
 * Returns server itself
 *
 * @type {SocketIO.Server}
 */
export declare const IO: (wrapper?: any) => ParameterDecorator;
/**
 * Returns socket
 *
 * @type {SocketIO.Socket}
 */
export declare const Socket: (wrapper?: any) => ParameterDecorator;
/**
 * Returns event arguments (excluding callback)(if it exists)
 *
 * @type {any[]}
 */
export declare const Args: (wrapper?: any) => ParameterDecorator;
/**
 * Returns ack callback function (if it exists)
 *
 * @type {Function}
 */
export declare const Ack: (wrapper?: any) => ParameterDecorator;
